ScriptGroup.script:
--Harvester mmhrv = mmhrv or {}
-- remembers room plant data into the herb db mmhrv.capturing = mmhrv.capturing or false -- harvests everything in every room we come across mmhrv.harvestingrooms = mmhrv.harvestingrooms or false -- only harvests upon arrival into a room mmhrv.harvestingarea = mmhrv.harvestingarea or false mmhrv.healrooms = mmhrv.healrooms or false mmhrv.forceplants = mmhrv.forceplants or false mmhrv.checkedplants = mmhrv.checkedplants or false
mmhrv.skills = {}
mmhrv.only = {}
