function mmhrv.phpTable(...) -- abuse to: http://richard.warburton.it local newTable,keys,values={},{},{} newTable.pairs=function(self) -- pairs iterator local count=0 return function() count=count+1 return keys[count],values[keys[count]] end end setmetatable(newTable,{ __newindex=function(self,key,value) if not self[key] then table.insert(keys,key) elseif value==nil then -- Handle item delete local count=1 while keys[count]~=key do count = count + 1 end table.remove(keys,count) end values[key]=value -- replace/create end, __index=function(self,key) return values[key] end }) for x=1,table.getn(arg) do for k,v in pairs(arg[x]) do newTable[k]=v end end return newTable end
function mmhrv.deepcopy(object) local lookup_table = {} local function _copy(object) if type(object) ~= "table" then return object elseif lookup_table[object] then return lookup_table[object] end local new_table = {} lookup_table[object] = new_table for index, value in pairs(object) do new_table[_copy(index)] = _copy(value) end return setmetatable(new_table, getmetatable(object)) end return _copy(object) end
function mmhrv.deleteLineP() deleteLine() tempLineTrigger(1,1,[[ if isPrompt() then deleteLine() end ]]) end
-- empty values for what are ok function mmhrv.splitwords(what) local t = {} for w in string.gmatch(what or "", "%w+") do t[#t+1] = w end
return (#t > 0) and t or nil end

Script.script:
function mmhrv.skillline()
	mmhrv.skills[mmhrv.skillmap[matches[2]:lower()] or matches[2]:lower()] = true
end

function mmhrv.herbsstart()
	mmhrv.skills = {}
end

function mmhrv.skillsdone()
	table.save(getMudletHomeDir() .. "/m&m_harvester", mmhrv.skills)
end

