mm.onprompt()
mm.watch = mm.watch or {}
for _, balance in pairs{"balance", "equilibrium", "leftarm", "rightarm"} do if mm["had_"..balance] and not mm.bals[balance] then raiseEvent("m&m lost balance", balance)
if mm.conf.showbaltimes then mm.watch["bal_"..balance] = mm.watch["bal_"..balance] or createStopWatch() startStopWatch(mm.watch["bal_"..balance]) end elseif not mm["had_"..balance] and mm.bals[balance] then raiseEvent("m&m got balance", balance)
if mm.conf.showbaltimes and mm.watch["bal_"..balance] then local s = stopStopWatch(mm.watch["bal_"..balance]) mm.echotime(s) end end
mm["had_"..balance] = nil end

