-- sample custom echo! Feel free to change the 'demo' name, so you can do 'mmconfig echotype <name>'.

--[[
function mm.echos.demo(...)
  decho("<206,222,215>(<214,206,221>m<161,143,176>&<214,206,221>m<206,222,215>)<252,251,254>: <249,244,254>" .. ... .. "\n")
end
function mm.echosd.demo()
	return "<249,244,254>"
end
]]

-- just shows how to suffix affs to the prompt so you know

function mm_display_affs()
	-- put two dashes before the line below so this function will work!
	if true then return end

	-- this if/elseif chain will append one aff at a time only
	if mm.affl.prone then
		echo("[[PRONE]] ")
	elseif mm.affl.paralysis then
		echo("[[PARALYSIS]] ")
	elseif mm.affl.crippledleftleg then
		echo("[[CRIPPLED LEFTLEG]] ")
	end

	-- these stand-alone ifs will allow for several affs to be suffixed
	if mm.affl.crippledrightleg then
		echo("[[CRIPPLED RIGHT LEG]] ")
	end

	if mm.affl.tangled then
		echo("[[TANGLED]] ")
	end
end

