Script.script:
function mm_check_land() if not mm.defc.land then return end
local t = { ["natural underground"] = true, ["forest"] = true, ["grasslands"] = true, ["path"] = true, ["valley"] = true, ["mountains"] = true, ["jungle"] = true, ["garden"] = true, ["trees"] = true, ["farmlands"] = true }
if t[gmcp.Room.Info.environment] then tempLineTrigger(0,1,[[fg("green") echo(" (land)") resetFormat()]]) else tempLineTrigger(0,1,[[fg("red") echo(" (land)") resetFormat()]]) end end

function mm.add_peopletracker_config() mm.me.watchfor = mm.me.watchfor or {}
mm.config.setoption("watchfor", { type = "string", check = function(what) if what:find("^%w+$") then return true end end, onset = function () local name = string.title(mm.conf.watchfor) -- we want nil, not false so 'or' doesn't help if mm.me.watchfor[name] then mm.me.watchfor[name] = nil else mm.me.watchfor[name] = true end
if mm.me.watchfor[name] then mm.echof("Added <0,255,0>%s%s to the watchfor list - use qwhow to see them.", name, mm.getDefaultColor()) else mm.echof("Removed %s from the watchfor list.", name) end raiseEvent("m&m watchfor name changed", name) end }) end

function mm.showupdatereminder(newversion)
  mm.announceupdates = nil

  mm.updatelabel = mm.updatelabel or Geyser.Label:new({
    name = "mm.updatelabel",
    x = "-340px", y = "-115px",
    width = "320px", height = "110px",
  })

  mm.doupdatelabel = mm.doupdatelabel or Geyser.Label:new({
    name = "mm.doupdatelabel",
    x = "25%", y = "-30px",
    width = "20%", height = "20px",
  }, mm.updatelabel)

  mm.dontupdatelabel = mm.dontupdatelabel or Geyser.Label:new({
    name = "mm.dontupdatelabel",
    x = "55%", y = "-30px",
    width = "20%", height = "20px",
  }, mm.updatelabel)

  -- grey to black shade, top to bottom
  mm.updatelabel:setStyleSheet([[
      margin: 0px;
      padding: 2px;

      /* Vertical gradient */
      background: qlineargradient(
          x1: 0, y1: 0, x2: 0, y2: 1,
          stop: 0 #3c3c3c, stop: 1 #232323
      );

      border: none;
      border-radius: 4px;

      color: #ffffff;

    qproperty-alignment: 'AlignTop | AlignHCenter';
    qproperty-wordWrap: true;
    font-family: 'Ubuntu','Calibri',serif;
  ]])

  mm.doupdatelabel:setStyleSheet([[
    border-radius: 4px;
    border-style: double;
    border-width: 2px;
    border-color: green;
    font-family: monospace;
  ]])

  mm.dontupdatelabel:setStyleSheet([[
    border-radius: 4px;
    border-style: double;
    border-width: 2px;
    border-color: grey;
    font-family: monospace;
  ]])

  mm.updatelabel:echo(string.format([[<p align="center" style="font-size:10pt; color:white">Hey, a new m&m (%s) has been downloaded and is available for install.<br><br>Install and restart now, or install later?<p>]], newversion))

  mm.doupdatelabel:echo([[<p align="center" style="font-size:8pt; color:white">Restart</p]])

  mm.dontupdatelabel:echo([[<p align="center" style="font-size:8pt; color:white">Later</p]])

  mm.updatelabel:show()
  mm.doupdatelabel:show()
  mm.dontupdatelabel:show()

  mm.doupdatelabel:setClickCallback("mm_doupdate_click")
  mm.dontupdatelabel:setClickCallback("mm_dontupdate_click")
end

function mm_dontupdate_click()
  mm.updatelabel:hide()
  mm.doupdatelabel:hide()
  mm.dontupdatelabel:hide()
end

function mm_doupdate_click()
  mm.updatelabel:echo([[<p align="center" style="font-size:10pt; color:white">Updating m&m...<p>]])
  mm.doupdatelabel:hide()
  mm.dontupdatelabel:hide()

  local name = mm.me.class

  if not (uninstallPackage and installPackage) then
    mm.updatelabel:echo([[<p align="center" style="font-size:10pt; color:white">Can't update your m&m. You didn't update to Mudlet 2.1, and your current Mudlet lacks the features necessary. Go update first please.<p>]])
    return
  end

  function mm_update_system()
    uninstallPackage(name.." m&m")
    installPackage(getMudletHomeDir().."/m&m/downloads/"..string.format("%s m&mf.zip", mm.me.class))

    mm.echof("m&m updated! Restart Mudlet, and you'll be all done.")

    mm.updatelabel:echo([[<p align="center" style="font-size:10pt; color:white">m&m updated!<br><br>Restart Mudlet, and you'll be all done.<p>]])
    mm.updatelabel:setStyleSheet([[
        margin: 0px;
        padding: 2px;

        /* Vertical gradient */
        background: qlineargradient(
            x1: 0, y1: 0, x2: 0, y2: 1,
            stop: 0 #3c3c3c, stop: 1 #232323
        );

        border: none;
        border-radius: 4px;

        color: #ffffff;

      qproperty-alignment: 'AlignVCenter | AlignHCenter';
      qproperty-wordWrap: true;
      font-family: 'Ubuntu','Calibri',serif;
    ]])
  end

  mm_update_system()
end
